import axios from "axios";

export default function Employer(){

  const postJob = async (e) => {
    e.preventDefault();

    const job = {
      title: e.target.title.value,
      company: e.target.company.value,
      location: e.target.location.value,
      description: e.target.description.value
    };

    await axios.post("http://localhost:5000/jobs", job);

    alert("Job Posted Successfully!");
  };

  return (
    <div>
      <h2>Post a Job</h2>

      <form onSubmit={postJob}>
        <input name="title" placeholder="Job Title" /><br/><br/>
        <input name="company" placeholder="Company Name" /><br/><br/>
        <input name="location" placeholder="Location" /><br/><br/>
        <textarea name="description" placeholder="Job Description"></textarea><br/><br/>

        <button>Post Job</button>
      </form>
    </div>
  );
}
